+function dxdt = plant(t,x,sys,ctrl)
+    
+    
+    % [J] = UpdateJacobian(x);
+    u = ctrl.u(t,x(1:6),ctrl.p(x(1:6),x(7:12)));
+    %  [T,W]=TensionDistribution(J,u);
+    q = x(1:6);
+    p = x(7:12);
+    q_hat1 = x(13:18);
+    p_hat1 = x(19:24);
+    distur_hat1 = x(25:30);
+    q_hat2 = x(31:36);
+    p_hat2 = x(37:42);
+    distur_hat2 = x(43:48);
+     % CESO State Update
+      z_hat1 = [sys.Hdq(q_hat1,p_hat1); sys.Hdp(q_hat1,p_hat1); distur_hat1];     % First Layer ESO State
+      z_hat2 = [sys.Hdq(q_hat2,p_hat2); sys.Hdp(q_hat2,p_hat2); distur_hat2];     % Second Layer ESO State
+
+      z_hat = [z_hat2(1:12);z_hat1(13:end)+z_hat2(13:end)];
+     % State Space Define
+     Ao = [zeros(6), eye(6),   zeros(6);    % ESO State Matrix
+           zeros(6), zeros(6), eye(6);
+           -eye(6), -sys.D(q), zeros(6); 
+            zeros(6),      zeros(6),         zeros(6)];
+    Bo = [zeros(6); sys.G(q); zeros(6)];
+    % ESO增益矩阵
+    alpha = 100;        % ESO 的扰动估计增益
+    l1 = 3*alpha;           % ESO 增益
+    l2 = 3*alpha^2;
+    l3 = alpha^3;   
+     L1 =[l1; l2; l3];
+     u_comp = u - z_hat(13:end);
+    
+    dxdt =  sys.dx(x(1:6),x(7:12),u_comp,t);%Define ODE
+    
+    z_hat1_p = Ao * z_hat1 + Bo* (u) + [300;300;300;5000;5000;5000;500000;500000;500000;].* [((abs(q - q_hat1)^0.8)*sign(q - q_hat1)+(q - q_hat1));...
+    (abs(q - z_hat1(1)/k)^0.7)*sign(q - z_hat1(1)/k)+((abs(q - z_hat1(1)/k)^0.7)*sign(q - z_hat1(1)/k)+(q - z_hat1(1)/k));...
+    (abs(q - z_hat1(1)/k)^0.5)*sign(q - z_hat1(1)/k)+(abs(q - z_hat1(1)/k)^0.5)*sign(q - z_hat1(1)/k)+(abs(q - z_hat1(1)/k)^0.5)*sign(q - z_hat1(1)/k)];
+
+    z_hat2_p = Ao * z_hat2 + Bo* (u)  + L1* (z_hat1(1)/k - z_hat2(1)/k);
+    
+    dxdt = [dxdt; z_hat1_p; z_hat2_p];
\ No newline at end of file

