+function dxdt = plant(t,x,sys,ctrl)
+    
+    
+    % [J] = UpdateJacobian(x);
+   
+    %  [T,W]=TensionDistribution(J,u);
+    q = x(1:6);
+    p = x(7:12);
+    q_hat1 = x(13:18);
+    p_hat1 = x(19:24);
+    distur_hat1 = x(25:30);
+    q_hat2 = x(31:36);
+    p_hat2 = x(37:42);
+    distur_hat2 = x(43:48);
+    q_hat3 = x(49:54);
+    p_hat3 = x(55:60);
+    distur_hat3 = x(61:66);
+   kMax = 4;
+    k4 = kMax*((x(67) >= kMax) + (x(67) < kMax).* (x(67) / kMax)); 
+    if ctrl.selector ==1
+     u = ctrl.u(k4,t,q,ctrl.p(q,p));
+
+      else
+       u = ctrl.u(t,q,ctrl.p(q,p));
