function dxdt = plant(t,x,sys,ctrl)
    
    
    
    ctrl.u(t,x(1:6),ctrl.p(x(1:6),x(7:12)));
      
    ode = @(t,x) sys.dx(x(1:6),x(7:12),ctrl.u(t,x(1:6),ctrl.p(x(1:6),x(7:12))),t);%Define ODE
